function markp% This marks the 1/x value on a plot where you click the mouse.% It will also display 1/x to the command window.set(gcf,'WindowButtonDownfcn',[...	'p=get(gca,''CurrentPoint'');',...	'x=p(1,1);',...	'y=p(1,2);',...	'space=0;',...	'disp(1/p(1,1));',...	'text(p(1,1),p(1,2)+space,num2str(1/x,''%.2f''),',...			'''HorizontalAlignment'',''center'',',...			'''FontSize'',12,''Color'',''r'')']) 