function markt% This marks the x value on a plot where you click the mouse.stfmt = '%.1f'; % format output to command windowset(gcf,'WindowButtonDownfcn',[...	'p=get(gca,''CurrentPoint'');',...	'x0=p(1,1);',...	'y0=p(1,2);',...	'space=0;',...	['disp(sprintf(''' stfmt ''',p(1,1)));'],...	'text(p(1,1),p(1,2)+space,''+'',',...			'''HorizontalAlignment'',''center'',',...			'''FontSize'',12,''Color'',''k'')'])%==========================================================================